import { useEffect, useState } from "react";
import { getModelos, getClientes, getTecnicos, getVehiculos, postDeleteVehiculo, postNewVehiculo, getVehiculosDeleted } from "../Utils/Fetchs";
import Listar from "./Listar";

function Vehiculos() {
    const [data, setData] = useState();
    const [filterData, setFilterData] = useState();
    const [deletedData, setDeletedData] = useState();
    const [showActive, setShowActive] = useState(true);
    const [currentVehiculo, setCurrentVehiculo] = useState();
    const [modelos, setModelos] = useState();
    const [clientes, setClientes] = useState();
    const [tecnicos, setTecnicos] = useState();

    useEffect(() => {
        updateData();
    }, []);

    function updateData() {
        getVehiculos()
            .then(response => {
                setData(response);
            });

        getVehiculosDeleted()
            .then(response => setDeletedData(response));

        getModelos()
            .then(response => {
                setModelos(response.filter(modelo => modelo.estado === true))
            })

        getTecnicos()
            .then(response => {
                setTecnicos(response.filter(tecnico => tecnico.estado === true))
            })

        getClientes()
            .then(response => {
                setClientes(response.filter(cliente => cliente.estado === true))
            })
    }

    function newVehiculo() {
        let patente = getPatente();
        let modelo = document.getElementById("modelo");
        let cliente = document.getElementById("cliente");
        let tecnico = document.getElementById("tecnico");

        if (patente.length === 6 && modelo.value && cliente.value && tecnico.value) {

            postNewVehiculo(patente, modelo.value, cliente.value, tecnico.value)
                .then(response => {
                    console.log(response);
                })

            const inputs = document.querySelectorAll("input");

            inputs.forEach(input => {
                input.value = "";
            });

            updateData();

        } else
            alert("Por favor, rellena todos los campos");
    };

    function deleteVehiculo() {
        postDeleteVehiculo(currentVehiculo.patente)
            .then(response => {
                setCurrentVehiculo();
            });

        setCurrentVehiculo();
        updateData();
    };

    function getPatente() {
        let divPatente = document.getElementById("patente_inputs");
        let inputs = divPatente.querySelectorAll("input");

        let patente = "";
        Array.from(inputs).map(input => (patente += input.value));

        return patente;
    }

    function handlePatenteChange(e) {
        let divPatente = document.getElementById("patente_inputs")
        let inputs = divPatente.querySelectorAll("input")
        let pos = parseInt(e.target.getAttribute('pos'));

        if (e.nativeEvent.inputType === "deleteContentBackward" && e.target.value.length === 0 && pos > 0) inputs[pos - 1].focus();
        else if (e.target.value.length >= 1) {
            e.target.value = e.target.value[0].toUpperCase();

            if (pos < 3 && !/^[A-Z]$/.test(e.target.value)) e.target.value = "";
            else if (pos >= 3 && !/^\d$/.test(e.target.value)) e.target.value = "";

            if (e.target.value.length > 0) {
                if (pos < 5) inputs[pos + 1].focus();
            }
        }

    }

    function getPatenteInputs() {
        let inputs = []

        for (let i = 0; i < 6; i++) {
            inputs.push(<input key={i} pos={i} type="text" onChange={handlePatenteChange} style={{ width: 20, margin: 10 }}></input>)
            if (i === 2) inputs.push(<p>-</p>)
        };

        return inputs;
    };

    function newVehiculoRender() {
        return (
            <div className="form">
                <h2>Crear vehiculo</h2>
                <div>
                    <p>Patente (AAA-123): </p>
                    <div id="patente_inputs" style={{ display: 'flex' }}>
                        {getPatenteInputs()}
                    </div>
                </div>
                <p>Modelo: </p>
                <select id="modelo" defaultValue={0}>
                    {modelos && modelos.map(modelo => (
                        <option value={modelo.id} key={modelo.id}>{modelo.nombre}</option>
                    ))}
                </select>

                <p>Cliente:</p>
                <select id="cliente" defaultValue={0}>
                    {clientes && clientes.map(cliente => (
                        <option value={cliente.id} key={cliente.id}>{cliente.nombre} {cliente.apellido}</option>
                    ))}
                </select>

                <p>Tecnico:</p>
                <select id="tecnico" defaultValue={0}>
                    {tecnicos && tecnicos.map(tecnico => (
                        <option value={tecnico.id} key={tecnico.id}>{tecnico.nombre} {tecnico.apellido}</option>
                    ))}
                </select>
                <button onClick={newVehiculo}>Guardar vehiculo</button>
            </div>
        )
    }

    function modifyVehiculoRender() {
        return (
            <div className="form">
                <h2>Eliminar vehiculo</h2>
                <p>Patente: {currentVehiculo.patente}</p>
                <p>Modelo: {currentVehiculo.modelo.nombre}</p>
                <p>Cliente: {currentVehiculo.cliente.nombre} {currentVehiculo.cliente.apellido}</p>
                <p>Tecnico: {currentVehiculo.tecnico.nombre} {currentVehiculo.tecnico.apellido}</p>
                <button onClick={deleteVehiculo}>Eliminar vehiculo</button>
            </div>
        );
    }

    function handleFilter(e) {
        let filtro = e.target.value;
        let regex = new RegExp(`^.*${filtro}.*$`);
        let filtrada = data.filter(vehiculo => regex.test(vehiculo.patente));

        setFilterData(filtrada);
    };

    function listarVehiculosRender(data, botton) {
        return (
            <div className="listado">
                <div style={{ display: 'flex', flexDirection: 'row', justifyContent: 'space-between' }}>
                    <h1>Vehiculos</h1>
                    <button onClick={() => setShowActive(!showActive)}>
                        {showActive ? 'Mostrar Eliminados' : 'Mostrar Activos'}
                    </button>
                </div>
                <input type="text" placeholder="Buscar" onChange={handleFilter}></input>

                <div style={{ overflow: 'scroll', height: 300 }}>
                    {data && data.map(vehiculo => (
                        <div key={vehiculo.patente} onClick={() => setCurrentVehiculo(vehiculo)} className="entidad">
                            <p>{vehiculo.patente}</p>
                        </div>
                    ))}
                </div>

                {botton}
                {currentVehiculo ? <button onClick={() => setCurrentVehiculo()}>Crear vehiculo</button> : <></>}
            </div>
        );
    }

    function viewRender(frames) {
        return (
            <div className="conteiner">{frames}</div>
        );
    }

    return (
        <div className="conteiner">
            {showActive ?
                data &&
                < Listar
                    data={data}
                    plural="Vehiculos"
                    singular="vehiculo"
                    currentItem={currentVehiculo}
                    setCurrentItem={setCurrentVehiculo}
                    itemKey="patente"
                    link="Eliminados"
                    setShowActive={setShowActive}
                    showActive={showActive} />
                : <Listar
                    data={deletedData}
                    plural="Vehiculos"
                    singular="vehiculo"
                    currentItem={currentVehiculo}
                    setCurrentItem={setCurrentVehiculo}
                    itemKey="patente"
                    link="Activos"
                    setShowActive={setShowActive}
                    showActive={showActive} />
            }
        </div >
    )
};

export default Vehiculos;
