import React, { useRef, useState } from 'react';
import './clienteTabla.css';
import { postDeleteCliente } from '../../Utils/Cliente';

function ClienteTabla(props) {
    function handleDeleteCliente(client) {
        let resultado = window.confirm(`¿Está seguro que desea eliminar el cliente ${client.nombre} ${client.apellido}?`)

        if (resultado) (
            postDeleteCliente(client.telefono)
                .then(response => {
                    props.setForceRender({});
                })
                .catch(error => { console.log(error) })
        );
    }

    const [searchTerm, setSearchTerm] = useState('');
    const [filterColumn, setFilterColumn] = useState(null);

    const handleColumnButtonClick = (columnName) => {
        setFilterColumn(columnName);
    }

    if (props.data) {
        const filteredData = props.data.filter((client) => {
            if (filterColumn) {
                const valueToSearch = client[filterColumn];
                return valueToSearch.toString().toLowerCase().includes(searchTerm.toLowerCase());
            } else {
                return true;
            }
        });

        return (
            <div>
                <input
                    type="text"
                    placeholder="Filtrar..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                />
                <table className="cliente-table">
                    <thead>
                        <tr>
                            <th>
                                <button onClick={() => handleColumnButtonClick('nombre')}>
                                    Nombre
                                </button>
                            </th>
                            <th>
                                <button onClick={() => handleColumnButtonClick('apellido')}>
                                    Apellido
                                </button>
                            </th>
                            <th>
                                <button onClick={() => handleColumnButtonClick('mail')}>
                                    Email
                                </button>
                            </th>
                            <th>
                                <button onClick={() => handleColumnButtonClick('telefono')}>
                                    Telefono
                                </button>
                            </th>
                            <th>
                                <button onClick={() => handleColumnButtonClick('direccion')}>
                                    Direccion
                                </button>
                            </th>
                            <th>
                                <button onClick={() => handleColumnButtonClick('ultima_visita')}>
                                    Ultima visita
                                </button>
                            </th>
                            <th>
                                Acciones
                            </th>
                        </tr>
                    </thead>
                    <tbody>
                        {filteredData.map((client, index) => (
                            <tr key={index}>
                                <td>{client.nombre}</td>
                                <td>{client.apellido}</td>
                                <td>{client.mail}</td>
                                <td>{client.telefono}</td>
                                <td className="direccion-cell">{client.direccion}</td>
                                <td>{client.ultima_visita.replace('T', ' ').replace('-', '/').replace('-', '/').slice(0, 16)}</td>
                                <td>
                                    <button>+</button>
                                    <button onClick={() => handleDeleteCliente(client)}>-</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        );
    }
    return null;
};

export default ClienteTabla;
