import { useState, useEffect } from 'react';
// ... otras importaciones ...

function App() {
  const [currentApp, setCurrentApp] = useState(<Vehiculos />);
  const [isNavExpanded, setNavExpanded] = useState(true); // Estado para controlar la expansión de la barra de navegación

  useEffect(() => {
    // Agrega un manejador de eventos para detectar el desplazamiento de la página
    const handleScroll = () => {
      if (window.scrollY > 0) {
        setNavExpanded(false); // Si la página se desplaza hacia abajo, retrae la barra de navegación
      } else {
        setNavExpanded(true); // Si la página vuelve arriba, expande la barra de navegación
      }
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll); // Limpia el manejador de eventos al desmontar el componente
    };
  }, []);

  return (
    <div className={`row ${isNavExpanded ? 'nav-expanded' : 'nav-retracted'}`}>
      <div className='nav-bar'>
        {/* ... tus elementos de navegación ... */}
      </div>
      <div className='content'>
        {currentApp}
      </div>
    </div>
  );
}

export default App;
