import { postNewCliente } from "../../Utils/Fetchs";
import { useState, useEffect } from "react";
import NewMarca from "../Marcas/NewMarca";

export default function NewCliente(props) {
    function newCliente() {
        let nombre = document.getElementById("nombre");
        let apellido = document.getElementById("apellido");
        let telefono = document.getElementById("telefono");

        if (nombre.value && apellido.value && telefono.value) {
            postNewCliente(nombre.value, apellido.value, telefono.value);
            nombre.value = apellido.value = telefono.value = "";
        } else
            alert("Por favor, rellena todos los campos");
    };

    return (
        <div className="form">
            <h2>Crear modelo</h2>
            <input id="nombre" type="text" placeholder="Nombre" />
            <select id="marca_nombre">
                {marcas && marcas.map(marca => (
                    <option values={marca.nombre} key={marca.nombre}>{marca.nombre}</option>
                ))}
            </select>
            <button onClick={() => setModalMarca(true)}>+</button>
            {modalMarca &&
                <div>
                    <button onClick={() => setModalMarca(false)}>Volver</button>
                    <NewMarca setModal={setModalMarca} updateData={updateData} />
                </div>
            }
            <button onClick={handleNewModelo}>Guardar modelo</button>
        </div>
    );

}
