import { useState } from "react";

export default function Listar(props) {
    const [filterData, setFilterData] = useState();

    function handleFilter(e) {
        let filtro = e.target.value;
        let regex = new RegExp(`^.*${filtro}.*$`);
        let filtrada = props.data.filter(item => regex.test(null));

        setFilterData(filtrada);
    };

    return (
        <div className="listado">
            <div style={{ display: 'flex', flexDirection: 'row', justifyContent: 'space-between' }}>
                <h1>{props.plural}</h1>
                {/* <button onClick={() => setShowActive(!showActive)}>
                    {showActive ? 'Mostrar Eliminados' : 'Mostrar Activos'}
                </button> */}
            </div>

            <input type="text" placeholder="Buscar" onChange={handleFilter}></input>

            <div style={{ overflow: 'scroll', height: 300 }}>
                {props.data && props.data.map(item => (
                    <div onClick={() => props.setCurrentItem(item)} className="entidad">
                        <p>probando</p>
                    </div>
                ))}
            </div>

            {botton}
            {props.currentItem ? <button onClick={() => props.setCurrentItem()}>Crear {props.singular}</button> : <></>}
        </div>
    );
}
