export const host = "http://localhost:8080/";

async function customFetch(url, token, body) {
    const headers = { 'Content-Type': 'application/json' };
    if (token) headers['Token'] = token;

    const requestInit = { headers: headers };

    if (body) {
        requestInit["method"] = "POST";
        requestInit["body"] = body;
    };

    try {
        const response = await fetch(url, requestInit);
        const responseText = await response.text();

        try {
            return JSON.parse(responseText);
        } catch (error) {
            console.log(error);
            return [];
        };
    } catch (error) {
        console.log(error);
        return [];
    };
};

export async function getFetch(url, token) {
    return customFetch(url);
};

export async function postFetch(url, token, body) {
    const headers = { 'Content-Type': 'application/json' };
    if (token) headers['Token'] = token;

    return customFetch(url, { method: 'POST', headers: headers, body: JSON.stringify(body) });
};
