import { useState } from 'react';
import Clientes from './Components/Clientes/Clientes';
import Marcas from './Components/Marcas/Marcas';
import Modelos from './Components/Modelos/Modelos';
import Tecnicos from './Components/Tecnicos/Tecnicos';
import Vehiculos from './Components/Vehiculos/Vehiculos';
import Ordenes from './Components/Ordenes/Ordenes';
import Servicios from './Components/Servicios/Servicios';
import './Styles/App.css';
import './Styles/cliente.css';

export default function App() {
  const [currentApp, setCurrentApp] = useState(<Vehiculos />);
  const [navOpen, setNavOpen] = useState(true);
  const secciones = [
    { componente: <Clientes />, largo: "Clientes", corto: "V" },
    { componente: <Marcas />, largo: "Marcas", corto: "V" },
    { componente: <Modelos />, largo: "Modelos", corto: "V" },
    { componente: <Vehiculos />, largo: "Vehiculos", corto: "V" },
    { componente: <Tecnicos />, largo: "Tecnicos", corto: "V" },
    { componente: <Servicios />, largo: "Servicios", corto: "V" },
    { componente: <Ordenes />, largo: "Ordenes", corto: "V" }
  ];

  return (
    <div className='app'>
      <div className={`nav-div-${navOpen}`}>
        <div className={`nav-bar`}>
          {secciones.map(seccion => (
            <div onClick={() => setCurrentApp(seccion.componente)}>{seccion.largo}</div>
          ))}
          <div />
          <div onClick={() => setNavOpen(!navOpen)}>Colapsar</div>
        </div>
      </div>
      <div className={`content-${!navOpen}`}>
        {currentApp}
      </div>
    </div >
  );
};
