import { useEffect, useState } from "react"
import Select from "../Select";
import DetailServicio from "./DetailServicio"
import { getServicios } from "../../Utils/Servicio";

export function FrameServicios(props) {
    const [servicios, setServicios] = useState();
    const [modal, setModal] = useState(false);

    useEffect(() => {
        getServicios().then(response => setServicios(response));
    }, [])

    function handleAddServicio(newServicio) {
        let existe = seleccionados.some(servicio => servicio.nombre === newServicio.nombre);

        if (!existe) {
            let newSeleccionados = [...seleccionados, newServicio];
            setSeleccionados(newSeleccionados);
            props.setServicios(newSeleccionados);
            setModal(false);
        } else alert(`El servicio "${newServicio.nombre}" ya esta en la orden.`)
    }

    return (
        <div>
            <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                <p>Servicios: </p>
                <div>{seleccionados.length > 0 ? <p>{seleccionados.length} seleccionados</p> : <p>Ninguno</p>}</div>
                <button onClick={() => setModal(true)}>Agregar</button>
            </div>
            <Select
                data={seleccionados}
                itemName={[["nombre"]]}
                itemKey="nombre" />

            {modal && <div>
                <button onClick={() => setModal(false)}>Volver</button>
                <Select
                    data={servicios}
                    itemName={[["nombre"]]}
                    itemKey="nombre"
                    setCurrentItem={handleAddServicio} />
            </div>}
        </div>
    )
}
