import { useEffect, useState } from "react";
import { getClientes, getClientesDeleted, postRecoverCliente } from "../../Utils/Cliente";
import ClienteTabla from "./ClienteTabla";

export default function Clientes() {
    const [activeData, setActiveData] = useState();
    const [deletedData, setDeletedData] = useState();
    const [frame, setFrame] = useState();
    const [currentCliente, setCurrentCliente] = useState();
    const [forceRender, setForceRender] = useState({});

    useEffect(() => {
        updateData();
    }, [forceRender]);

    useEffect(() => {
        let telefono = document.getElementById("telefono_cliente");
        let nombre = document.getElementById("nombre_cliente");
        let apellido = document.getElementById("apellido_cliente");
        let mail = document.getElementById("mail_cliente");
        let direccion = document.getElementById("direccion_cliente");
        let ultima_visita = document.getElementById("ultima_visita_cliente");

        telefono.value = "";
        nombre.value = "";
        apellido.value = "";
        mail.value = "";
        direccion.value = "";
        ultima_visita.value = "";

    }, [currentCliente])

    function updateData() {
        getClientes().then(response => setActiveData(response));
        getClientesDeleted().then(response => setDeletedData(response));
    }

    function handleSetView() {
        setFrame(!frame);
        setCurrentCliente();
    }

    function handleRecoverCliente() {
        postRecoverCliente(currentCliente.telefono)
            .then(response => {
                setForceRender({});
                setCurrentCliente();
                alert(`Cliente ${currentCliente.nombre} ${currentCliente.apellido} recuperado con exito.`)
            })
            .catch(error => console.log(error))
    }

    function RecoverCliente(client) {
        return (
            <div>
                <h2>Reucuperar cliente</h2>
                <p>Nombre: {client.nombre}</p>
                <p>Apellido: {client.apellido}</p>
                <p>Telefono: {client.telefono}</p>
                <p>Mail: {client.mail}</p>
                <p>Direccion: {client.direccion}</p>
                <p>Ultima visita: {client.ultima_visita}</p>
                <button onClick={handleRecoverCliente}>Recuperar</button>
            </div>
        );
    }

    const tablaActivos = activeData && <ClienteTabla
        data={activeData}
        setForceRender={setForceRender}
        title="Clientes"
        changeFrame={<button onClick={() => setFrame(false)}>Recuperar</button>} />;

    const tablaEliminados = deletedData && <ClienteTabla
        data={deletedData}
        setForceRender={setForceRender}
        title="Eliminados"
        changeFrame={<button onClick={() => setFrame(true)}>Volver</button>} />;

    return (
        <div>
            {frame ? activeData && tablaActivos : deletedData && tablaEliminados}
            {/* <DetailCliente cliente={currentCliente} setForceRender={setForceRender} setCurrentCliente={setCurrentCliente} : <RecoverCliente /> */}
        </div >
    )
};
