import React, { useEffect, useState } from 'react';
import editIcon from '../../Static/edit.png';
import deleteIcon from '../../Static/delete.png';

export default function Table(props) {
    const { fetchGet, fetchDelete, title, DetailComponent, table, changeFrame, vocabulary, id } = props;

    const [items, setItems] = useState([]);
    const [forceRender, setForceRender] = useState();
    const [modal, setModal] = useState();
    const [currentItem, setCurrentItem] = useState();

    useEffect(() => {
        fetchGet().then(response => setItems(response));
    }, [forceRender]);

    const [searchTerm, setSearchTerm] = useState('');
    const [filterColumn, setFilterColumn] = useState(null);

    const handleColumnButtonClick = (columnName) => {
        if (filterColumn) {
            if (columnName.toString() !== filterColumn.toString()) setFilterColumn(columnName);
            else setFilterColumn(null);
        } else setFilterColumn(columnName);
    }

    const filteredData = items.filter((item) => {
        if (filterColumn) {
            let valueToSearch = item;

            for (let i of filterColumn) {
                valueToSearch = valueToSearch[i];
            }
            return valueToSearch.toString().toLowerCase().includes(searchTerm.toLowerCase());
        } else {
            return true;
        }
    });

    function getAtributte(item, index) {
        for (let i of index) {
            item = item[i];
        } return item;
    }

    return (
        <div>
            <div className='head-table'>
                <div className='row row-line'>
                    <h1>{title}</h1>
                    {changeFrame}
                </div>
                <div className='row'>
                    <input
                        type="search"
                        placeholder="Buscar..."
                        value={searchTerm}
                        className='finder'
                        onChange={(e) => setSearchTerm(e.target.value)}
                    />
                    {/* <button className='head-button' onClick={() => setModal(true)}>Crear</button> */}
                </div>
            </div>
            <table className="cliente-table">
                <thead>
                    <tr>
                        {table.map(group => (
                            <th>
                                <button className={filterColumn && filterColumn.toString() === group.filter.toString() ? 'selected' : ''} onClick={() => handleColumnButtonClick(group.filter)}>
                                    {group.column}
                                </button>
                            </th>
                        ))}
                        <th>
                            Acciones
                        </th>
                    </tr>
                </thead>
                <tbody>
                    {filteredData.map((item, index) => (
                        <tr key={index}>
                            {table.map(group => <td>{getAtributte(item, group.row)}</td>)}
                            <td className='action-box'>
                                <button className='action-button delete-button' onClick={() => handleDeleteItem(item)}>
                                    <img src={deleteIcon} title="Eliminar" />
                                </button>
                                <button className='action-button edit-button' onClick={() => handleSetModal(item)}>
                                    <img src={editIcon} alt="Editar" />
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div >
    );
};
