import React, { useState, useEffect } from 'react';

export default function ClienteTabla({ data }) {
    const [searchTerm, setSearchTerm] = useState('');
    const [visibleData, setVisibleData] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const itemsPerPage = 10;

    // Actualizar la lista de datos visibles cuando cambia la página
    const updateVisibleData = () => {
        const startIndex = (currentPage - 1) * itemsPerPage;
        const endIndex = startIndex + itemsPerPage;
        setVisibleData(data.slice(startIndex, endIndex));
    };

    // Llamar a updateVisibleData cada vez que cambie el término de búsqueda
    useEffect(() => {
        updateVisibleData();
    }, [searchTerm, data, currentPage]);

    // Llamar a updateVisibleData cuando cambie la página
    useEffect(() => {
        updateVisibleData();
    }, [currentPage]);

    const handlePageChange = (newPage) => {
        setCurrentPage(newPage);
    };

    return (
        <div>
            <input
                type="text"
                placeholder="Filtrar..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
            />
            <table>
                <thead>
                    <tr>
                        <th>
                            <button onClick={() => handleColumnButtonClick('nombre')}>
                                Nombre
                            </button>
                        </th>
                        <th>
                            <button onClick={() => handleColumnButtonClick('apellido')}>
                                Apellido
                            </button>
                        </th>
                        <th>
                            <button onClick={() => handleColumnButtonClick('mail')}>
                                Email
                            </button>
                        </th>
                        <th>
                            <button onClick={() => handleColumnButtonClick('telefono')}>
                                Telefono
                            </button>
                        </th>
                    </tr>
                </thead>
                <tbody>
                    {visibleData
                        .filter(client => {
                            return Object.values(client).some(value =>
                                value.toString().toLowerCase().includes(searchTerm.toLowerCase())
                            );
                        })
                        .map((client, index) => (
                            <tr key={index}>
                                <td>{client.nombre}</td>
                                <td>{client.apellido}</td>
                                <td>{client.mail}</td>
                                <td>{client.telefono}</td>
                            </tr>
                        ))}
                </tbody>
            </table>
            <div className="pagination">
                <button onClick={() => handlePageChange(currentPage - 1)} disabled={currentPage === 1}>
                    Anterior
                </button>
                <span>Página {currentPage}</span>
                <button
                    onClick={() => handlePageChange(currentPage + 1)}
                    disabled={currentPage * itemsPerPage >= data.length}
                >
                    Siguiente
                </button>
            </div>
        </div>
    );
}
