import { useState, useEffect } from 'react';
import './App.css';
import Clientes from './Components/Clientes/Clientes';
import Marcas from './Components/Marcas/Marcas';
import Modelos from './Components/Modelos/Modelos';
import Tecnicos from './Components/Tecnicos/Tecnicos';
import Vehiculos from './Components/Vehiculos/Vehiculos';
import Ordenes from './Components/Ordenes/Ordenes';
import Servicios from './Components/Servicios/Servicios';
import './Styles/navBar.css';

function App() {
  const [currentApp, setCurrentApp] = useState(<Vehiculos />);
  const [isNavExpanded, setNavExpanded] = useState(true); // Estado para controlar la expansión de la barra de navegación

  useEffect(() => {
    // Agrega un manejador de eventos para detectar el desplazamiento de la página
    const handleScroll = () => {
      if (window.scrollY > 0) {
        setNavExpanded(false); // Si la página se desplaza hacia abajo, retrae la barra de navegación
      } else {
        setNavExpanded(true); // Si la página vuelve arriba, expande la barra de navegación
      }
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll); // Limpia el manejador de eventos al desmontar el componente
    };
  }, []);

  return (
    <div className={`row ${isNavExpanded ? 'nav-expanded' : 'nav-retracted'}`}>
      <div className='nav-bar'>
        <div onClick={() => setCurrentApp(<Vehiculos />)}>Vehiculos</div>
        <div onClick={() => setCurrentApp(<Marcas />)}>Marcas</div>
        <div onClick={() => setCurrentApp(<Modelos />)}>Modelos</div>
        <div onClick={() => setCurrentApp(<Tecnicos />)}>Tecnicos</div>
        <div onClick={() => setCurrentApp(<Clientes />)}>Clientes</div>
        <div onClick={() => setCurrentApp(<Servicios />)}>Servicios</div>
        <div onClick={() => setCurrentApp(<Ordenes />)}>Ordenes</div>
      </div>
      <div className='content'>
        {currentApp}
      </div>
    </div>
  );
}

export default App;
