import { useState, useEffect } from "react";


export default function DetailCliente(props) {
    const [cliente, setCliente] = useState();
    const [modelo, setModelo] = useState();
    const [patente, setPatente] = useState(["", "", "", "", "", ""]);
    const [focus, setFocus] = useState();

    function handleNewVehiculo() {
        let año = document.getElementById("año");

        if (getPatente().length === 6 && modelo && cliente && año.value) {
            if (2024 >= parseInt(año.value) && parseInt(año.value) >= 1886) {
                postNewVehiculo(getPatente(), modelo.id, cliente.id, año.value)
                    .then(response => {
                        alert(`El vehiculo ${getPatente()} se creo con exito.`);
                        props.setForceRender({});
                    })
                    .catch(error => alert(`El vehiculo con la patente ${getPatente()} ya existe.`));

                const inputs = document.querySelectorAll("input");
                inputs.forEach(input => {
                    input.value = "";
                });
                setPatente(["", "", "", "", "", ""]);
                setModelo();
                setCliente();

            } else alert("El año del vehiculo debe ser entre 1886 y 2023.")

        } else
            alert("Por favor, rellena todos los campos.");
    };




    function handleDeleteCliente() {
        let resultado = window.confirm(`¿Está seguro que desea eliminar el cliente ${props.cliente.nombre} ${props.cliente.apellido}?`)

        if (resultado) (
            postDeleteVehiculo(props.vehiculo.patente)
                .then(response => {
                    props.setForceRender({})
                    props.setCurrentVehiculo();
                })
                .catch(error => { console.log(error) })
        );
    }

    function handleTelefonoChange(e) {
        if (!(e.nativeEvent.inputType === "deleteContentBackward")) {
            if (!/^\d*$/.test(e.target.value) || e.target.value.length >= 10) e.target.value = e.target.value.slice(0, -1)
        }
    }

    return (
        <div>
            <div>
                <p>Nombre: </p>
                <input id="nombre" type="text" placeHolder="Nombre"></input>
            </div>
            <div>
                <p>Apellido: </p>
                <input id="apellido" type="text" placeHolder="Apellido"></input>
            </div>
            <div>
                <p>Telefono:</p>
                <input id="telefono" type="text" onChange={handleTelefonoChange} />
            </div>

            {props.cliente ?
                <div>
                    <button><p>Modificar cliente</p></button>
                    <button onClick={handleDeleteCliente}><p>Eliminar cliente</p></button>
                </div>
                :
                <button onClick={handleNewVehiculo}><p>Crear vehiculo</p></button>
            }
        </div>
    );
}
