import { useEffect, useState } from "react";
import { getModelos, getClientes, getTecnicos, getVehiculos, postDeleteVehiculo, postNewVehiculo, getVehiculosDeleted } from "../Utils/Fetchs";
import Listar from "./Listar";
import NewModelo from "./NewModelo";
import NewVehiculo from "./NewVehiculo";

export default function Vehiculos() {
    const [activeData, setActiveData] = useState();
    const [deletedData, setDeletedData] = useState();
    const [frame, setFrame] = useState();
    const [currentVehiculo, setCurrentVehiculo] = useState();
    const [modelos, setModelos] = useState();
    const [clientes, setClientes] = useState();
    const [modalModelo, setModalModelo] = useState();

    useEffect(() => {
        updateData();
    }, []);

    function updateData() {
        getVehiculos()
            .then(response => setActiveData(response));

        getVehiculosDeleted()
            .then(response => setDeletedData(response));

        getModelos()
            .then(response => setModelos(response));

        getClientes()
            .then(response => setClientes(response));
    }

    function handleSetView() {
        setFrame(!frame);
        setCurrentVehiculo();
    }

    function newVehiculo() {
        let patente = getPatente();
        let modelo = document.getElementById("modelo");
        let cliente = document.getElementById("cliente");
        let tecnico = document.getElementById("tecnico");

        if (patente.length === 6 && modelo.value && cliente.value && tecnico.value) {

            postNewVehiculo(patente, modelo.value, cliente.value, tecnico.value)
                .then(response => {
                    console.log(response);
                })

            const inputs = document.querySelectorAll("input");

            inputs.forEach(input => {
                input.value = "";
            });

            updateData();

        } else
            alert("Por favor, rellena todos los campos");
    };

    function deleteVehiculo() {
        postDeleteVehiculo(currentVehiculo.patente)
            .then(response => {
                setCurrentVehiculo();
            });

        setCurrentVehiculo();
        updateData();
    };

    function getPatente() {
        let divPatente = document.getElementById("patente_inputs");
        let inputs = divPatente.querySelectorAll("input");

        let patente = "";
        Array.from(inputs).map(input => (patente += input.value));

        return patente;
    }

    function handlePatenteChange(e) {
        let divPatente = document.getElementById("patente")
        let inputs = divPatente.querySelectorAll("input")
        let pos = parseInt(e.target.getAttribute('pos'));

        if (e.nativeEvent.inputType === "deleteContentBackward" && e.target.value.length === 0 && pos > 0) inputs[pos - 1].focus();
        else if (e.target.value.length >= 1) {
            e.target.value = e.target.value[0].toUpperCase();

            if (pos < 3 && !/^[A-Z]$/.test(e.target.value)) e.target.value = "";
            else if (pos >= 3 && !/^\d$/.test(e.target.value)) e.target.value = "";

            if (e.target.value.length > 0) {
                if (pos < 5) inputs[pos + 1].focus();
            }
        }

    }

    function PatenteRender() {
        let inputs = []

        for (let i = 0; i < 6; i++) {
            inputs.push(<input key={i} pos={i} type="text" onChange={handlePatenteChange} style={{ width: 20, margin: 10 }}></input>)
            if (i === 2) inputs.push(<p>-</p>)
        };

        return inputs;
    };


    function ModifyVehiculoRender() {
        return (
            <div className="form">
                <h2>Eliminar vehiculo</h2>
                <p>Patente: {currentVehiculo.patente}</p>
                <p>Modelo: {currentVehiculo.modelo.nombre}</p>
                <p>Cliente: {currentVehiculo.cliente.nombre} {currentVehiculo.cliente.apellido}</p>
                <p>Tecnico: {currentVehiculo.tecnico.nombre} {currentVehiculo.tecnico.apellido}</p>
                <button onClick={deleteVehiculo}>Eliminar vehiculo</button>
            </div>
        );
    }

    function NewVehiculoRender() {
        return (
            <div>
                <div>
                    <p>Patente ej (AAA-123): </p>
                    <div id="patente" style={{ display: 'flex' }}><PatenteRender /></div>
                </div>
                <div>
                    <p>AÃ±o: </p>
                    <input type="text" />
                </div>
                <div>
                    <p>Modelo: </p>
                    <select>
                        {modelos && modelos.map(modelo => (
                            <option values={modelo.nombre} key={modelo.id}>{modelo.nombre}</option>
                        ))}
                    </select>
                    <button onClick={() => setModalModelo(true)}>+</button>
                    {modalModelo &&
                        <div>
                            <button onClick={() => setModalModelo(false)}>Volver</button>
                            <NewModelo />
                        </div>
                    }
                </div>
                <div>
                    <p>Cliente: </p>
                </div>
                <button>Crear vehiculo</button>
            </div>
        );
    }


    function DetailRender() {
        if (!frame) {
            return currentVehiculo ? <ModifyVehiculoRender /> : <NewVehiculo />
        } else return <div>Recuperar vehiculo</div>
    }

    const listarActivos = <Listar
        data={activeData}
        titulo="Vehiculos"
        itemName="patente"
        itemKey="patente"
        buttonView={<button onClick={handleSetView}>Eliminados</button>}
        buttonCurrent={<button onClick={() => setCurrentVehiculo()}>Crear vehiculo</button>}
        currentItem={currentVehiculo}
        setCurrentItem={setCurrentVehiculo} />

    const listarDeleted = <Listar
        data={deletedData}
        titulo="Eliminados"
        itemName="patente"
        itemKey="patente"
        buttonView={<button onClick={handleSetView}>Activos</button>}
        currentItem={currentVehiculo}
        setCurrentItem={setCurrentVehiculo} />

    return (
        <div className="conteiner">
            {!frame ? activeData && listarActivos : deletedData && listarDeleted}
            <DetailRender />
        </div >
    )
};
