import React, { useEffect, useState } from 'react';
import editIcon from '../../Static/edit.png';
import deleteIcon from '../../Static/delete.png';
import Table from './Table';

export default function CreateTable(props) {
    const { fetchGet, fetchDelete, title, DetailComponent, table, changeFrame, vocabulary, id } = props;

    const accionModificar = {
        component: <img src={backIcon} alt="Editar" />,
        className: "action-button back-button",
        funcion: handleRecoverItem
    }

    return (<Table
        items={items}
        title={"Eliminados"}
        changeFrame={changeFrame}
        acciones={[{
            component: <img src={backIcon} alt="Editar" />,
            className: "action-button back-button",
            funcion: handleRecoverItem
        }]}
        table={table} />)

    const [items, setItems] = useState([]);
    const [forceRender, setForceRender] = useState();
    const [modal, setModal] = useState();
    const [currentItem, setCurrentItem] = useState();

    useEffect(() => {
        fetchGet().then(response => setItems(response));
    }, [forceRender]);

    const [searchTerm, setSearchTerm] = useState('');
    const [filterColumn, setFilterColumn] = useState(null);

    const handleColumnButtonClick = (columnName) => {
        if (filterColumn) {
            if (columnName.toString() !== filterColumn.toString()) setFilterColumn(columnName);
            else setFilterColumn(null);
        } else setFilterColumn(columnName);
    }

    function handleDeleteItem(item) {
        let resultado = window.confirm(`¿Está seguro que deseas eliminar ${vocabulary.pronombre} ${vocabulary.singular} con ${vocabulary.id} ${item[vocabulary.id]}?`)
        if (resultado) (
            fetchDelete(item[id])
                .then(response => {
                    setForceRender({})
                    setCurrentItem();
                    alert(`Se elimino exitosamente ${vocabulary.pronombre} ${vocabulary.singular} con ${vocabulary.id} ${item[vocabulary.id]}.`)
                })
                .catch(error => { console.log(error) })
        );
    }

    const filteredData = items.filter((item) => {
        if (filterColumn) {
            let valueToSearch = item;

            for (let i of filterColumn) {
                valueToSearch = valueToSearch[i];
            }
            return valueToSearch.toString().toLowerCase().includes(searchTerm.toLowerCase());
        } else {
            return true;
        }
    });

    function handleSetModal(item) {
        if (!modal) {
            setCurrentItem(item);
            setModal(true);
        } else {
            setModal(false);
            setCurrentItem();
        };
    };

    function getAtributte(item, index) {
        for (let i of index) {
            item = item[i];
        } return item;
    }

    return (
        <div>
            {modal &&
                <div class="modal-background">
                    <div class="modal-content h100 scroll">
                        <div className='row w100'>
                            <button className='modal-close-button' onClick={handleSetModal}>Volver</button>
                        </div>
                        <DetailComponent item={currentItem} />
                    </div>
                </div>
            }

            <div className='head-table'>
                <div className='row row-line'>
                    <h1>{title}</h1>
                    {changeFrame}
                </div>
                <div className='row'>
                    <input
                        type="search"
                        placeholder="Buscar..."
                        value={searchTerm}
                        className='finder'
                        onChange={(e) => setSearchTerm(e.target.value)}
                    />
                    <button className='head-button' onClick={() => setModal(true)}>Crear</button>
                </div>
            </div>
            <table className="cliente-table">
                <thead>
                    <tr>
                        {table.map(group => (
                            <th>
                                <button className={filterColumn && filterColumn.toString() === group.filter.toString() ? 'selected' : ''} onClick={() => handleColumnButtonClick(group.filter)}>
                                    {group.column}
                                </button>
                            </th>
                        ))}
                        <th>
                            Acciones
                        </th>
                    </tr>
                </thead>
                <tbody>
                    {filteredData.map((item, index) => (
                        <tr key={index}>
                            {table.map(group => <td>{getAtributte(item, group.row)}</td>)}
                            <td className='action-box'>
                                <button className='action-button delete-button' onClick={() => handleDeleteItem(item)}>
                                    <img src={deleteIcon} title="Eliminar" />
                                </button>
                                <button className='action-button edit-button' onClick={() => handleSetModal(item)}>
                                    <img src={editIcon} alt="Editar" />
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div >
    );
};
